const $ = new Env('健康社区兑换');
let commodityId = $.getdata('commodityId') || 4;//兑换商品编号

const notify = $.isNode() ? require('./sendNotify') : '';
//Node.js用户请在jdCookie.js处填写京东ck;
const jdCookieNode = $.isNode() ? require('./jdCookie.js') : '';

let cookiesArr = [],
    cookie = '',
    message;
if ($.isNode()) {
    Object.keys(jdCookieNode).forEach((item) => {
        cookiesArr.push(jdCookieNode[item])
    })
    if (process.env.JD_DEBUG && process.env.JD_DEBUG === 'false') console.log = () => { };
} else {
    let cookiesData = $.getdata('CookiesJD') || "[]";
    cookiesData = jsonParse(cookiesData);
    cookiesArr = cookiesData.map(item => item.cookie);
    cookiesArr.reverse();
    cookiesArr.push(...[$.getdata('CookieJD2'), $.getdata('CookieJD')]);
    cookiesArr.reverse();
    cookiesArr = cookiesArr.filter(item => item !== "" && item !== null && item !== undefined);
}

!(async () => {
    if (!cookiesArr[0]) {
        $.msg($.name, '【提示】请先获取京东账号一cookie\n直接使用NobyDa的京东签到获取', 'https://bean.m.jd.com/bean/signIndex.action', {
            "open-url": "https://bean.m.jd.com/bean/signIndex.action"
        });
        return;
    }
    await jdhealth_getCommodities();
    await $.wait(1000);
    for (let i = 0; i < cookiesArr.length; i++) {
        if (cookiesArr[i]) {
            cookie = cookiesArr[i];
            $.UserName = decodeURIComponent(cookie.match(/pt_pin=(.+?);/) && cookie.match(/pt_pin=(.+?);/)[1])
            $.index = i + 1;
            $.isLogin = true;
            $.nickName = '';
            message = '';
            console.log(`\n******开始【京东账号${$.index}】${$.nickName || $.UserName}*********\n`);
            if (!$.isLogin) {
                $.msg($.name, `【提示】cookie已失效`, `京东账号${$.index} ${$.nickName || $.UserName}\n请重新登录获取\nhttps://bean.m.jd.com/bean/signIndex.action`, {
                    "open-url": "https://bean.m.jd.com/bean/signIndex.action"
                });
                continue
            }

            await jdhealth_exchange();
        }
    }
})()
    .catch((e) => {
        $.log('', `❌ ${$.name}, 失败! 原因: ${e}!`, '')
    })
    .finally(() => {
        $.done();
    })
async function jdhealth_getCommodities() {
    console.log('获取京豆兑换列表：' + "\n");
    var headers = {
        "Cookie": cookiesArr[0],
        "User-Agent": "jdapp;iPhone;10.0.8;14.6;91eaf40b15b21cbc604261ac99c6c7d46e6dcd1c;network/wifi;model/iPhone9,1;addressid/3991000806;appBuild/167728;jdSupportDarkMode/0;Mozilla/5.0 (iPhone; CPU iPhone OS 14_6 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148;supportJDSHWK/1",
        "Content-Type": "application/x-www-form-urlencoded",
        "Host": "api.m.jd.com"
    };
    var commodities = await doPost("https://api.m.jd.com/?functionId=jdhealth_getCommodities&client=wh5", headers);
    if (commodities.code == 0) {
        //京豆兑换专区
        var jBeans = commodities.data.result.jBeans;
        for (let item of jBeans) {
            console.log("商品编号:" + item.id + "--京豆:" + item.title + "--兑换所需能量:" + item.exchangePoints + "\n");
        }
        //一元兑换专区
        var physicals = commodities.data.result.physicals;
    } else {
        console.log('获取兑换列表失败：' + commodities.msg);
    }

}
async function jdhealth_exchange() {
    try {
        var headers = {
            "Cookie": cookie,
            "User-Agent": "jdapp;iPhone;10.0.8;14.6;91eaf40b15b21cbc604261ac99c6c7d46e6dcd1c;network/wifi;model/iPhone9,1;addressid/3991000806;appBuild/167728;jdSupportDarkMode/0;Mozilla/5.0 (iPhone; CPU iPhone OS 14_6 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148;supportJDSHWK/1",
            "Content-Type": "application/x-www-form-urlencoded",
            "Host": "api.m.jd.com"
        };
        var body = { commodityType: 2, commodityId: commodityId };
        var jdhealth_exchange = await doPost("https://api.m.jd.com/?functionId=jdhealth_exchange&client=wh5&body=" + encodeURIComponent(JSON.stringify(body)) + "", headers);
        if (jdhealth_exchange.data.success == true) {
            console.log("兑换商品成功：" + jdhealth_exchange.data.result.jingBeanNum + "京豆");
            message += "兑换商品成功：" + jdhealth_exchange.data.result.jingBeanNum + "京豆" + "\n\n";
        } else {
            console.log('兑换商品失败：' + jdhealth_exchange.data.bizMsg);
            message += '兑换商品失败：' + jdhealth_exchange.data.bizMsg + "\n\n";
        }
        await showMsg()
    } catch (e) {
        $.logErr(e)
    }
}

function doGet(url) {
    return new Promise(async resolve => {
        $.get({
            url: url,
            headers: {
                "Accept": "application/json,text/plain, */*",
                "Content-Type": "application/x-www-form-urlencoded",
                "Accept-Encoding": "gzip, deflate, br",
                "Accept-Language": "zh-cn",
                "Connection": "keep-alive",
                "Cookie": cookie,
                "User-Agent": "jdapp;iPhone;10.0.0;14.4.2;91eaf40b15b21cbc604261ac99c6c7d46e6dcd1c;network/wifi;model/iPhone9,1;addressid/3991000806;appBuild/167671;jdSupportDarkMode/0;Mozilla/5.0 (iPhone; CPU iPhone OS 14_4_2 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Mobile/15E148;supportJDSHWK/1",
                "Host": "api.m.jd.com",
                "Referer": "https://h5.m.jd.com/babelDiy/Zeus/2H5Ng86mUJLXToEo57qWkJkjFPxw/index.html?fromName=main_channel&lng=114.546270&lat=36.547645&sid=db0bfa4a5e643d65b5ed5031f0e1f87w&un_area=5_148_34050_55978"
            }
        }, (err, resp, data) => {
            try {
                if (err) {
                    console.log(`${url}——${JSON.stringify(err)}`);
                    console.log(`${$.name} API请求失败，请检查网路重试`);
                } else {
                    data = JSON.parse(data);
                }
            } catch (e) {
                $.logErr(e, resp)
            } finally {
                resolve(data);
            }
        })
    })

}
function doPost(url, headers, body) {
    return new Promise(async resolve => {
        //console.log(body);
        $.post({
            url: url,
            Body: body,
            headers: headers
        }, (err, resp, data) => {
            try {
                if (err) {
                    console.log(`${url}——${JSON.stringify(err)}`);
                    console.log(`${$.name} API请求失败，请检查网路重试`);
                } else {
                    data = JSON.parse(data);
                }
            } catch (e) {
                $.logErr(e, resp)
            } finally {
                resolve(data);
            }
        })
    })
}
function safeGet(data) {
    try {
        if (typeof JSON.parse(data) == "object") {
            return true;
        }
    } catch (e) {
        console.log(e);
        console.log(`京东服务器访问数据为空，请检查自身设备网络情况`);
        return false;
    }
}
function jsonParse(str) {
    if (typeof str == "string") {
        try {
            return JSON.parse(str);
        } catch (e) {
            console.log(e);
            $.msg($.name, '', '请勿随意在BoxJs输入框修改内容\n建议通过脚本去获取cookie')
            return [];
        }
    }
}
function TotalBean() {
    return new Promise(async resolve => {
        const options = {
            "url": `https://wq.jd.com/user/info/QueryJDUserInfo?sceneval=2`,
            "headers": {
                "Accept": "application/json,text/plain, */*",
                "Content-Type": "application/x-www-form-urlencoded",
                "Accept-Encoding": "gzip, deflate, br",
                "Accept-Language": "zh-cn",
                "Connection": "keep-alive",
                "Cookie": cookie,
                "Referer": "https://wqs.jd.com/my/jingdou/my.shtml?sceneval=2",
                "User-Agent": $.isNode() ? (process.env.JD_USER_AGENT ? process.env.JD_USER_AGENT : (require('./USER_AGENTS').USER_AGENT)) : ($.getdata('JDUA') ? $.getdata('JDUA') : "jdapp;iPhone;9.2.2;14.2;%E4%BA%AC%E4%B8%9C/9.2.2 CFNetwork/1206 Darwin/20.1.0")
            }
        }
        $.post(options, (err, resp, data) => {
            try {
                if (err) {
                    console.log(`${JSON.stringify(err)}`)
                    console.log(`${$.name} API请求失败，请检查网路重试`)
                } else {
                    if (data) {
                        data = JSON.parse(data);
                        if (data['retcode'] === 13) {
                            $.isLogin = false; //cookie过期
                            return
                        }
                        $.nickName = data['base'].nickname;
                    } else {
                        console.log(`京东服务器返回空数据`)
                    }
                }
            } catch (e) {
                $.logErr(e, resp)
            } finally {
                resolve();
            }
        })
    })
}
function showMsg() {
    return new Promise(resolve => {
        $.msg($.name, '', `京东账号${$.index}${$.nickName}\n${message}`);
        resolve()
    })
}

